import React, { Component } from 'react'
import moment from 'moment'
import { Formik, Form, Field } from 'formik'

class TodoComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id : this.props.match.params.id,
            description : 'Learn Forms',
            targetDate : moment(new Date()).format('YYYY-MM-DD')
        }
        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)
    }

    validate(values) {
        let errors = {}
        if (!values.description) {
            return errors.description = "Enter a Description"
        } else if (values.description.length < 5) {
            return errors.description = "Enter a characters atleast 5 in  Description"
        }

        if (!moment(values.targetDate).isValid()) {
            return errors.targetDate = "Enter a valid target date"
        }
        return errors
    }

    onSubmit(values) {
        console.log(values)
    }

    render() {
        /*this is one of the most useful feature in JavaScript and known as destructuring*/
        let {description, targetDate} = this.state
       /* let targetDate = this.state.targetDate */

        return (
            <div>
                <h1>Todo</h1>
                <div className="container">
                    <Formik initialValues={{description,targetDate}}
                    onSubmit={this.onSubmit} validate={this.validate}
                    >
                        {
                            (props) => (
                                <Form>
                                    <fieldset className="form-group">
                                        <label>Description</label>
                                        <Field className={"form-control"} type="text" name="description"/>
                                    </fieldset>

                                    <fieldset className="form-group">
                                        <label>Target Date</label>
                                        <Field className={"form-control"} type="date" name="targetDate"/>
                                    </fieldset>
                                    <button className="btn btn-success" type="submit">Save</button>
                                </Form>
                            )
                        }
                    </Formik>
                </div>
                 {/*Todo component for id - {this.props.match.params.id}*/}
            </div>
            )
    }
}

export default TodoComponent
