import axios from 'axios'
export const USER_NAME_SESSION_ATTRIBUTE_NAME = 'authenticatedUser'

class AuthenticationService {


    executeBasicAuthenticationService(username, password) {
        return axios.get('http://localhost:8080/basicauth',
            {headers: {authorisation: this.createBasicAuthTocken(username, password)}})
    }


    createBasicAuthTocken(username, password) {
        return  'Basic ' + window.btoa(username + ":" + password)
    }

    registerSuccessfulLogin(username, password) {
        sessionStorage.setItem(USER_NAME_SESSION_ATTRIBUTE_NAME, username)
        this.setupAxiosInterceptors()
    }

    logout() {
        sessionStorage.removeItem(USER_NAME_SESSION_ATTRIBUTE_NAME)
    }

    isUserLoggedIn() {
        let user = sessionStorage.getItem(USER_NAME_SESSION_ATTRIBUTE_NAME)
        if (user === null) return false
        return true
    }

    getLoggedInUsername() {
        let user = sessionStorage.getItem(USER_NAME_SESSION_ATTRIBUTE_NAME)
        if (user === null) return ''
        return user
    }

    setupAxiosInterceptors(basicAuthHeader) {
        let username = 'in28minutes'
        let password = 'dummy'
        basicAuthHeader = 'Basic ' + window.btoa(username + ":" + password)

        axios.interceptors.request.use(
            (config) => {
                if (this.isUserLoggedIn()) { config.headers.authorization = basicAuthHeader }
                return config
            }
        )
    }
}

export default new AuthenticationService();
