import React, {Component} from 'react';
import {BrowserRouter as Router, Route, Switch, Link} from 'react-router-dom'
import "./TodoApp.css"
import "../../bootstrap.css"
import HeaderComponent from "./HeaderComponent";
import FooterComponent from "./FooterComponent"
import ListTodosComponent from "./ListTodosComponent"
import LoginComponent from "./LoginComponent"
import LougoutComponent from "./LougoutComponent"

class TodoApp extends Component {
    render() {
        return (
            <div>
                {/*My Todo Application*/}
                <div className="TodoApp">

                    <Router>
                        <HeaderComponent/>
                        <Switch>
                            <Route path="/" exact component={LoginComponent}/>
                            <Route path="/login" component={LoginComponent}/>
                            <Route path="/welcome/:name" component={WelcomeComponent}/>
                            <Route path="/todos" component={ListTodosComponent}/>
                            <Route path="/logout" component={LougoutComponent}/>
                            <Route component={ErrorComponent}/>
                        </Switch>
                        <FooterComponent/>
                    </Router>

                    <br/>
                    {/* <LoginComponent></LoginComponent>
                    <WelcomeComponent></WelcomeComponent>*/}
                </div>
            </div>
        )
    }
}


class WelcomeComponent extends Component {
    render() {
        return (
            <div> Welcome {this.props.match.params.name}.
                Please navigate here for Todos list <Link to="/todos"> Here </Link>
            </div>
        )
    }
}

function ErrorComponent() {
    return <div>Error happened, please contact abcd-xyz</div>
}



