import React, {Component} from "react";
import TodoDataService from "../../api/todo/TodoDataService.js"
import AuthenticationService from "./AuthenticationService.js"

class ListTodosComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todos: [
                /*{id: 1, description: 'Visiting India soon', done: false, targetDate: new Date()},
                {id: 2, description: 'Become an expert in React', done: false, targetDate: new Date()},
                {id: 3, description: 'Learn to do Swadhyay', done: false, targetDate: new Date()}*/
            ]

        }
    }
    componentDidMount() {
        let username = AuthenticationService.getLoggedInUsername()
        TodoDataService.retriveAllTodos(username)
            .then(
                response => {
                    //console.log(response)
                    this.setState({todos : response.data})
                }
             )
    }

    render() {
        return (
            <div>
                <h1>List Todos</h1>
                <div className="container">
                    <table className="table">
                        <thead>
                        <tr>
                            <th>Description</th>
                            <th>is Completed?</th>
                            <th>Target Date</th>
                            <th>Delete</th>
                        </tr>
                        </thead>
                        {
                            this.state.todos.map(
                                todo =>
                                    <tr key={todo.id}>
                                        <td>{todo.description}</td>
                                        <td>{todo.done.toString()}</td>
                                        <td>{todo.targetDate.toString()}</td>
                                        <td><button className="btn btn-warning">Delete</button></td>

                                    </tr>
                            )
                        }
                        <tbody>
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}
export default ListTodosComponent
