import React, {Component} from 'react'
import moment from 'moment'
import {Formik, Form, Field, ErrorMessage} from 'formik'
import AuthenticationService from "./AuthenticationService";
import TodoDataService from "../../api/todo/TodoDataService";

class TodoComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: this.props.match.params.id,
            description: 'Learn Forms',
            targetDate: moment(new Date()).format('YYYY-MM-DD')
        }
        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)
    }

    componentDidMount() {
        let username = AuthenticationService.getLoggedInUsername()
        TodoDataService.retriveTodo(username, this.state.id)
            .then(r => this.setState({
                description: r.data.description,
                targetDate: moment(r.data.targetDate).format('YYYY-MM-DD')
            }))
    }

    validate(values) {
        let errors = {}
        if (!values.description) {
            errors.description = "Enter a Description"
        } else if (values.description.length < 5) {
            errors.description = "Enter a characters at least 5 in  Description"
        }

        if (!moment(values.targetDate).isValid()) {
            errors.targetDate = "Enter a valid target date"
        }
        return errors
    }

    onSubmit(values) {
        let username = AuthenticationService.getLoggedInUsername()
        TodoDataService.updateTodo(username, this.state.id, {
            id: this.state.id,
            description : values.description,
            targetDate : values.targetDate
        })
        console.log(values)
    }

    render() {
        /*this is one of the most useful feature in JavaScript and known as destructuring*/
        let {description, targetDate} = this.state
        /* let targetDate = this.state.targetDate */
        return (
            <div>
                <h1>Todo</h1>
                <div className="container">
                    <Formik
                        initialValues={{description, targetDate}}
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        validate={this.validate}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name="description" component="div" className="alert alert-warning">
                                    </ErrorMessage>
                                    <ErrorMessage name="targetDate" component="div" className="alert alert-warning">
                                    </ErrorMessage>
                                    <fieldset className="form-group">
                                        <label>Description</label>
                                        <Field className={"form-control"} type="text" name="description"/>
                                    </fieldset>

                                    <fieldset className="form-group">
                                        <label>Target Date</label>
                                        <Field className={"form-control"} type="date" name="targetDate"/>
                                    </fieldset>
                                    <button className="btn btn-success" type="submit">Save</button>
                                </Form>
                            )
                        }
                    </Formik>
                </div>
            </div>
        )
    }
}

export default TodoComponent
