import React, {Component} from "react";

class LoginComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username: 'in28minutes',
            password: '',
            showSuccessMessage: false,
            hasLoginFailed: false
        }
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
        /*this.handleUsernameChange = this.handleUsernameChange.bind(this)
        this.handlePasswordChange = this.handlePasswordChange.bind(this)*/
    }

    handleChange(event) {
        console.log(this.state)
        this.setState({[event.target.name]: event.target.value})
    }

    loginClicked() {
        if (this.state.username === "in28minutes" && this.state.password === "dummy") {
            this.props.history.push(`/Welcome/${this.state.username}`)
            console.log("Successful")
            /*this.setState({showSuccessMessage: true, hasLoginFailed: false})*/
        } else {
            console.log("Failed")
            this.setState({showSuccessMessage: false, hasLoginFailed: true})
        }
    }

    /* handleUsernameChange(event) {
          console.log(event.target.value)
          this.setState({username: event.target.value})
      }

      handlePasswordChange(event) {
          console.log(event.target.value)
          this.setState({password: event.target.value})
      }*/

    render() {
        return (
            <>
                {/*    <ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed}></ShowInvalidCredentials>
                <ShowValidCredential showSuccessMessage={this.state.showSuccessMessage}></ShowValidCredential>*/}
                {this.state.hasLoginFailed && <div> invalid Credentials </div>}
                {this.state.showSuccessMessage && <div> Successful Login </div>}
                <br/>
                User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange}/>
                Password: <input type="text" name="password" value={this.state.password} onChange={this.handleChange}/>
                <button onClick={this.loginClicked}>Login</button>
            </>
        )
    }

}

export default LoginComponent
