import React, {Component} from 'react';
import {BrowserRouter as Router, Route, Switch, Link} from 'react-router-dom'
import "./TodoApp.css"
import "../../bootstrap.css"

class TodoApp extends Component {
    render() {
        return (
            <div>
                {/*My Todo Application*/}
                <div className="TodoApp">

                    <Router>
                        <HeaderComponent/>
                        <Switch>
                            <Route path="/" exact component={LoginComponent}/>
                            <Route path="/login" component={LoginComponent}/>
                            <Route path="/welcome/:name" component={WelcomeComponent}/>
                            <Route path="/todo" component={ListTodosComponent}/>
                            <Route component={ErrorComponent}/>
                        </Switch>
                        <FooterComponent/>
                    </Router>

                    <br/>
                    {/* <LoginComponent></LoginComponent>
                    <WelcomeComponent></WelcomeComponent>*/}
                </div>
            </div>
        )
    }
}


class ListTodosComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todos: [
                {id: 1, description: 'Visiting India soon',done: false, targetDate: new Date()},
                {id: 2, description: 'Become an expert in React',done: false, targetDate: new Date()},
                {id: 3, description: 'Learn to do Swadhyay',done: false, targetDate: new Date()}
            ]

        }
    }

    render() {
        return (
            <div>
                <h1>List Todos</h1>
                <table>
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Description</th>
                        <th>is Completed?</th>
                        <th>Target Date</th>
                    </tr>
                    </thead>
                    {
                        this.state.todos.map(
                            todo =>
                                <tr>
                                    <td>{todo.id}</td>
                                    <td>{todo.description}</td>
                                    <td>{todo.done.toString()}</td>
                                    <td>{todo.targetDate.toString()}</td>
                                </tr>
                        )
                    }
                    <tbody>

                    </tbody>
                </table>
            </div>
        )
    }
}

class HeaderComponent extends Component {
    render() {
        return (
            <header>
                <nav className="navbar navbar-expand-md">
                    <ul className="navbar-nav">
                        <li className="nav-link">Home</li>
                        <li className="nav-link">Todos</li>
                    </ul>

                    <ul className="navbar-nav">
                        <li className="nav-link">Login</li>
                        <li className="nav-link">Logout</li>
                    </ul>
                </nav>

            </header>

        )
    }
}

class FooterComponent extends Component {
    render() { return ( <div><hr/>  Footer  </div> ) }
}

class WelcomeComponent extends Component {
    render() {
        return (
            <div> Welcome {this.props.match.params.name}.
                Please navigate here for Todos list <Link to="/todo"> Here </Link>
            </div>
        )
    }
}

function ErrorComponent() {
    return <div>Error happened, please contact abcd-xyz</div>
}

class LoginComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username: 'in28minutes',
            password: '',
            showSuccessMessage: false,
            hasLoginFailed: false
        }
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
        /*this.handleUsernameChange = this.handleUsernameChange.bind(this)
        this.handlePasswordChange = this.handlePasswordChange.bind(this)*/
    }

    handleChange(event) {
        console.log(this.state)
        this.setState({[event.target.name]: event.target.value})
    }

    loginClicked() {
        if (this.state.username === "in28minutes" && this.state.password === "dummy") {
            this.props.history.push(`/Welcome/${this.state.username}`)
            console.log("Successful")
            /*this.setState({showSuccessMessage: true, hasLoginFailed: false})*/
        } else {
            console.log("Failed")
            this.setState({showSuccessMessage: false, hasLoginFailed: true})
        }
    }

    /* handleUsernameChange(event) {
          console.log(event.target.value)
          this.setState({username: event.target.value})
      }

      handlePasswordChange(event) {
          console.log(event.target.value)
          this.setState({password: event.target.value})
      }*/

    render() {
        return (
            <>
                {/*    <ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed}></ShowInvalidCredentials>
                <ShowValidCredential showSuccessMessage={this.state.showSuccessMessage}></ShowValidCredential>*/}
                {this.state.hasLoginFailed && <div> invalid Credentials </div>}
                {this.state.showSuccessMessage && <div> Successful Login </div>}

                <br/>
                User Name: <input type="text" name="username" value={this.state.username}
                                  onChange={this.handleChange}/>
                Password: <input type="text" name="password" value={this.state.password}
                                 onChange={this.handleChange}/>
                <button onClick={this.loginClicked}>Login</button>
            </>
        )
    }

}

/*//Functional Components
function ShowInvalidCredentials(props) {
    if (props.hasLoginFailed) {
        return <div> invalid Credentials </div>
    } else {
        return null
    }
}

//Functional Component
function ShowValidCredential(props) {
    if (props.showSuccessMessage) {
        return <div>Successfull Login</div>
    } else {
        return null
    }
}*/

export default TodoApp;
